from pydantic_settings import BaseSettings, SettingsConfigDict
from typing import Annotated, Any, Literal

from pydantic import (
    AnyUrl,
    BeforeValidator,
    computed_field,
    MariaDBDsn,
    Field
)

from pydantic_core import MultiHostUrl


def parse_cors(v: Any) -> list[str] | str:
    if isinstance(v, str) and not v.startswith("["):
        return [i.strip() for i in v.split(",")]
    elif isinstance(v, list | str):
        return v
    raise ValueError(v)


class Settings(BaseSettings):
    model_config = SettingsConfigDict(
        env_file='.env',
        env_file_encoding='utf-8',
        extra="ignore",
        env_ignore_empty = True,
    )
    DOMAIN: str = 'localhost'
    ENVIRONMENT: Literal["local", "staging", "production"] = "local"
    JWT_SECRET_KEY: str

    @computed_field
    @property
    def server_host(self) -> str:
        # Use HTTPS for anything other than local development
        if self.ENVIRONMENT == "local":
            return f"http://{self.DOMAIN}"
        return f"https://{self.DOMAIN}"

    BACKEND_CORS_ORIGINS: Annotated[list[AnyUrl] | str, BeforeValidator(parse_cors)] = Field(default=lambda: [])

    MARIADB_USERNAME: str
    MARIADB_PASSWORD: str
    MARIADB_SERVER: str
    MARIADB_PORT: int
    MARIADB_DATABASE: str

    @computed_field  # type: ignore[misc]
    @property
    def SQLALCHEMY_DATABASE_URI(self) -> MariaDBDsn | MultiHostUrl:
        return MultiHostUrl.build(
            scheme="mysql+pymysql",
            username=self.MARIADB_USERNAME,
            password=self.MARIADB_PASSWORD,
            host=self.MARIADB_SERVER,
            port=self.MARIADB_PORT,
            path=self.MARIADB_DATABASE
        )

from pydantic_settings import BaseSettings, SettingsConfigDict
from typing import Annotated, Any, Literal

from pydantic import (
    AnyUrl,
    BeforeValidator,
    computed_field,
    Field
)


def parse_cors(v: Any) -> list[str] | str:
    if isinstance(v, str) and not v.startswith("["):
        return [i.strip() for i in v.split(",")]
    elif isinstance(v, list | str):
        return v
    raise ValueError(v)


class Settings(BaseSettings):
    model_config = SettingsConfigDict(
        env_file='.env',
        env_file_encoding='utf-8',
        extra="ignore",
        env_ignore_empty = True,
    )
    DOMAIN: str = 'localhost'
    ENVIRONMENT: Literal["local", "staging", "production"] = "local"
    JWT_SECRET_KEY: str

    @computed_field
    @property
    def server_host(self) -> str:
        # Use HTTPS for anything other than local development
        if self.ENVIRONMENT == "local":
            return f"http://{self.DOMAIN}"
        return f"https://{self.DOMAIN}"

    BACKEND_CORS_ORIGINS: Annotated[list[AnyUrl] | str, BeforeValidator(parse_cors)] = Field(default=lambda: [])

    MARIADB_USERNAME: str
    MARIADB_PASSWORD: str
    MARIADB_SERVER: str
    MARIADB_PORT: int
    MARIADB_DATABASE: str

    @computed_field
    @property
    def SQLALCHEMY_DATABASE_URI(self) -> str:
        # Return simple string instead of complex MariaDBDsn to prevent validation hanging
        return f"mysql+pymysql://{self.MARIADB_USERNAME}:{self.MARIADB_PASSWORD}@{self.MARIADB_SERVER}:{self.MARIADB_PORT}/{self.MARIADB_DATABASE}"

import subprocess
import sys
from core.config_loader import settings

def setup_mysql():
    try:
        subprocess.run(['mysql', '--version'], check=True, capture_output=True)
    except (subprocess.CalledProcessError, FileNotFoundError):
        print("Please, install mysql and try again.")
        sys.exit(1)

    try:
        subprocess.run(['systemctl', 'is-active', 'mysql'], check=True, capture_output=True)
    except subprocess.CalledProcessError:
        try:
            subprocess.run(['systemctl', 'is-active', 'mariadb'], check=True, capture_output=True)
        except subprocess.CalledProcessError:
            print("MySQL/MariaDB server isn't running or doesn't exist. If it doesn't, install it and try again.")
            sys.exit(1)

    check_user_cmd = (f"SELECT COUNT(*) FROM mysql.user WHERE user='{settings.MARIADB_USERNAME}' "
                      f"AND host='{settings.MARIADB_SERVER}';")
    result = subprocess.run([
        'sudo', 'mysql', '-u', 'root', '-e', check_user_cmd
    ], check=True, capture_output=True, text=True)

    user_exists = '1' in result.stdout

    if not user_exists:
        create_user_commands = [
            f"CREATE USER '{settings.MARIADB_USERNAME}'@'{settings.MARIADB_SERVER}:{settings.MARIADB_PORT}'"
            f" IDENTIFIED BY '{settings.MARIADB_PASSWORD}';",
            "GRANT ALL PRIVILEGES ON *.* TO "
            f"'{settings.MARIADB_USERNAME}'@'{settings.MARIADB_SERVER}:{settings.MARIADB_PORT}';",
            "FLUSH PRIVILEGES;"
        ]

        for cmd in create_user_commands:
            try:
                subprocess.run([
                    'sudo', 'mysql', '-u', 'root', '-e', cmd
                ], check=True, capture_output=True)
            except Exception as e:
                print(f"There was a problem creating \"{settings.MARIADB_USERNAME}\""
                      f" user on mysql:\n{e}\n\nCommands used:\n"
                      f"{'; '.join(create_user_commands)}")
    return True

