DROP DATABASE IF EXISTS raffles; CREATE DATABASE raffles; USE raffles;
CREATE TABLE proyects ( id INT AUTO_INCREMENT PRIMARY KEY, name VARCHAR(100) NOT NULL, description TEXT, creation_date TIMESTAMP DEFAULT CURRENT_TIMESTAMP, isActive BOOLEAN DEFAULT true );
CREATE TABLE rafflesSet ( id INT AUTO_INCREMENT PRIMARY KEY, proyect_id INTEGER, name VARCHAR(100) NOT NULL, type VARCHAR(8) NOT NULL CHECK (type IN ('online', 'physical')), init INTEGER NOT NULL, final INTEGER NOT NULL, unit_price INTEGER NOT NULL, creation_date TIMESTAMP DEFAULT CURRENT_TIMESTAMP, CONSTRAINT valid_numbers CHECK (init < final), CONSTRAINT unique_set UNIQUE (proyect_id, name), FOREIGN KEY (proyect_id) REFERENCES proyects(id) );
CREATE TABLE buyers ( id INT AUTO_INCREMENT PRIMARY KEY, name VARCHAR(100) NOT NULL, email VARCHAR(100) UNIQUE NOT NULL, phone VARCHAR(20), register_date TIMESTAMP DEFAULT CURRENT_TIMESTAMP );
CREATE TABLE raffles ( set_id INTEGER NOT NULL, number INTEGER NOT NULL, sell_date TIMESTAMP, buyer_id INTEGER, payment_method VARCHAR(50) CHECK (payment_method in ('CASH', 'CARD', 'TRANSFER', 'OTHER')), state VARCHAR(20) DEFAULT 'available' CHECK (state IN ('available', 'sold', 'reserved')), FOREIGN KEY (buyer_id) REFERENCES buyers(id), FOREIGN KEY (set_id) REFERENCES rafflesSet(id), PRIMARY KEY (set_id, number), UNIQUE KEY unique_number (number) );
