from fastapi import HTTPException


def get_record(db, Model, _id, model_name):
    record = db.query(Model).filter(Model.id == _id).first()
    if not record:
        raise HTTPException(status_code=404, detail=f"{model_name} not found")
    return record

from fastapi import HTTPException


def get_record(db, Model, _id, model_name, id_field="id"):
    """Get a record by ID or custom field name."""
    if id_field == "id":
        record = db.query(Model).filter(Model.id == _id).first()
    elif id_field == "number":
        record = db.query(Model).filter(Model.number == _id).first()
    else:
        # Generic approach for other field names
        field = getattr(Model, id_field)
        record = db.query(Model).filter(field == _id).first()

    if not record:
        raise HTTPException(status_code=404, detail=f"{model_name} not found")
    return record

from fastapi import APIRouter, Depends, HTTPException, Query
from sqlalchemy.exc import IntegrityError
from sqlalchemy.orm import Session
from database.connection import get_db
from models.buyer import Buyer
from routes import get_record
from schemas.buyer import BuyerCreate, BuyerDelete, BuyerUpdate

router = APIRouter()


@router.post("/buyer")
def create_buyer(buyer: BuyerCreate, db: Session = Depends(get_db)):
    new_buyer = Buyer(
        name=buyer.name,
        phone=buyer.phone,
        email=str(buyer.email)
    )

    db.add(new_buyer)
    try:
        db.commit()
    except IntegrityError:
        db.rollback()
        raise HTTPException(
            status_code=400,
            detail="There's already a buyer with that name and phone."
        )

    db.refresh(new_buyer)
    return {
        "id": new_buyer.id,
        "name": new_buyer.name,
        "email": new_buyer.email,
        "phone": new_buyer.phone
    }


@router.get("/buyer")
def get_buyer(
    id: int = Query(..., ge=1),
    db: Session = Depends(get_db)
):
    return get_record(db, Buyer, id, "Buyer")


@router.get("/buyers")
def get_buyers(
    limit: int = 0,
    db: Session = Depends(get_db)
):
    if limit > 0:
        return db.query(Buyer).limit(limit).all()
    elif limit == 0:
        return db.query(Buyer).all()
    else:
        raise HTTPException(
            status_code=400,
            detail="Limit must be a non-negative integer."
        )


@router.patch("/buyer")
@router.put("/buyer")
def update_buyer(
    updates: BuyerUpdate,
    db: Session = Depends(get_db)
):
    buyer_record = get_record(db, Buyer, updates.id, "Buyer")

    for field, value in updates.model_dump(exclude_unset=True).items():
        setattr(buyer_record, field, value)

    db.commit()
    db.refresh(buyer_record)
    return buyer_record


@router.delete("/buyer")
def delete_buyer(buyer: BuyerDelete, db: Session = Depends(get_db)):
    if buyer.id:
        buyer_record = buyer_record = get_record(db, Buyer, id, "Buyer")
        db.delete(buyer_record)
        db.commit()
        return {"message": "Buyer deleted successfully"}
    else:
        buyer_record = db.query(Buyer).filter(
            (Buyer.name == buyer.name) & (Buyer.phone == buyer.phone)
        ).first()
        if not buyer_record:
            raise HTTPException(
                status_code=404,
                detail="There isn't any buyer with that pair of name and number."
            )
        db.delete(buyer_record)
        db.commit()
        return {"message": "Buyer deleted successfully"}

