CREATE SCHEMA IF NOT EXISTS raffles_schema;

SET search_path TO raffles_schema, public;

CREATE TABLE proyects (
    id SERIAL PRIMARY KEY,
    name VARCHAR(100) NOT NULL,
    description TEXT,
    creation_date TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    isActive BOOL DEFAULT true
);

CREATE TABLE rafflesSet (
    id SERIAL PRIMARY KEY,
    proyect_id INTEGER REFERENCES proyects(id),
    name VARCHAR(100) NOT NULL,
    type VARCHAR(8) NOT NULL CHECK (type IN ('online', 'physical')),
    init INTEGER NOT NULL,
    final INTEGER NOT NULL,
    unit_price INTEGER NOT NULL ,
    creation_date TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    CONSTRAINT valid_numbers CHECK (init < final),
    CONSTRAINT unique_set UNIQUE (proyect_id, name)
);

CREATE TABLE buyers (
    id SERIAL PRIMARY KEY,
    name VARCHAR(100) NOT NULL,
    email VARCHAR(100) UNIQUE NOT NULL,
    phone VARCHAR(20),
    register_date TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

CREATE TABLE raffles ( -- Units
    set_id INTEGER NOT NULL REFERENCES rafflesSet(id),
    number INTEGER NOT NULL,
    sell_date TIMESTAMP,
    buyer_id INTEGER,
    payment_method VARCHAR(50) CHECK (payment_method in ('CASH', 'CARD', 'TRANSFER', 'OTHER')),
    state VARCHAR(20) DEFAULT 'available' CHECK (state IN ('available', 'sold', 'reserved')),
    FOREIGN KEY (buyer_id) REFERENCES buyers(id),
    PRIMARY KEY (set_id, number)
);

CREATE OR REPLACE FUNCTION generate_raffles()
RETURNS TRIGGER AS $$
DECLARE
    next_number INTEGER;
    count_numbers INTEGER;
BEGIN

    SELECT COALESCE(MAX(number), 0) + 1 INTO next_number FROM raffles;

    -- Calculate how many numbers we need to generate
    count_numbers := NEW.final - NEW.init + 1;

    -- Update the rafflesSet with the actual init and final values
    UPDATE rafflesSet
    SET init = next_number,
        final = next_number + count_numbers - 1
    WHERE id = NEW.id;

    -- Insert the raffle numbers
    INSERT INTO raffles (set_id, number)
    SELECT NEW.id, generate_series(next_number, next_number + count_numbers - 1);

    RETURN NEW;
END;
$$ LANGUAGE plpgsql;

CREATE TRIGGER trig_generate_raffles
AFTER INSERT ON rafflesSet
FOR EACH ROW
EXECUTE FUNCTION generate_raffles();
